import React from 'react'
import { IonSlides, IonSlide, IonModal, IonImg } from '@ionic/react'
import ProductImagesItem from '../components/ProductImagesItem'

interface Slide {
	pageName: string
	src: string
	title: string
	id: number
}

interface ProductSliderProps {
	slides: Slide[]
	isOpen: boolean
	openOnSlide: number
	handleCloseSlider: () => void
}

const ProductSlider: React.FC<ProductSliderProps> = ({ slides, isOpen, openOnSlide, handleCloseSlider }) => {
	const handleChangeSlide = (direction) => {
		console.log('direction', direction)
	}

	return (
		// <IonModal
		// 	isOpen={isOpen}
		// 	cssClass='ProductSlider productModal'
		// >
		<div>
			<IonSlides>
				{slides.map(slide => (
					<IonSlide key={slide.id}>
						<IonImg
							key={slide.id}
							class="ProductSlider__img"
							src={slide.src}
							/>
					</IonSlide>
				))}
			</IonSlides>
			<button onClick={handleCloseSlider}>Close</button>
			<button onClick={() => handleChangeSlide('prev')}>Prev</button>
			<button onClick={() => handleChangeSlide('next')}>Next</button>
		</div>
		// </IonModal>
	)
}

export default ProductSlider
