import React, { useState } from 'react';
import { Menu, getMenuItems } from '../data/menuItems';
import {
  IonContent,
  IonHeader,
  IonList,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
  IonButtons,
  IonButton,
  IonIcon,
  IonPopover
} from '@ionic/react';
import './Home.css';
import MenuListItem from '../components/MenuListItem';
import 'tippy.js/dist/tippy.css';
import { SocialSharing } from '@ionic-native/social-sharing/';
import { AppRate } from '@ionic-native/app-rate/';



const Home: React.FC = () => {

  const [menuItems, setMenuItems] = useState<Menu[]>([]);
  const [showPopover, setShowPopover] = useState(false);

  const [shareAppObj] = useState(SocialSharing);
  const shareApp = () => {
    console.log('Email!');
    shareAppObj.canShareViaEmail().then(() => {
      shareAppObj.shareViaEmail('Body', 'Subject', ['recipient@example.org']).then(() => {
        // Success!

      }).catch(() => {
        // Error!
      });
    }).catch(() => {

    });
  };
  const [rateAppObj] = useState(AppRate);
  const rateApp = () =>{
    console.log(rateAppObj);
    rateAppObj.navigateToAppStore();
  }

  useIonViewWillEnter(() => {
    const mnu = getMenuItems();
    setMenuItems(mnu);
  });

  const refresh = (e: CustomEvent) => {
    setTimeout(() => {
      e.detail.complete();
    }, 3000);
  };



  return (
    <IonPage id="home-page">
      <IonHeader>
        <IonToolbar>
          <IonTitle>Frank Allart Products</IonTitle>
          <IonButtons slot="end">
          <IonButton class="actionButton" onClick={() => setShowPopover(true)}>
            <IonIcon slot="icon-only" src="assets/icon/ellipsis-vertical-outline.svg"/>
          </IonButton>
        </IonButtons>
        <IonPopover
            isOpen={showPopover}
            cssClass='my-custom-class'
            onDidDismiss={e => setShowPopover(false)}>
            <a href="/Notifications"><IonIcon slot="icon-only" src="assets/icon/notifications-outline.svg"/>Notifications</a>
            <hr id="solid"></hr>
            <a href="/Search"><IonIcon slot="icon-only" src="assets/icon/search-outline.svg"/>Search</a>
            <hr id="solid"></hr>
            <IonButton id="rateAppbtn" onClick={rateApp} ><IonIcon  slot="icon-only" src="assets/icon/thumbs-up-outline.svg"/>Rate Now</IonButton>
            <hr id="solid"></hr>
            <IonButton id="shareAppbtn" onClick={shareApp} ><IonIcon slot="icon-only" src="assets/icon/share-social-outline.svg"/>Share Now</IonButton>
          </IonPopover>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonRefresher slot="fixed" onIonRefresh={refresh}>
          <IonRefresherContent></IonRefresherContent>
        </IonRefresher>
        <IonList>
         {menuItems.map(m => <MenuListItem key={m.id} menu={m} />)}
        </IonList>
      </IonContent>
    </IonPage>



  );
};

export default Home;
