import React, { useState } from 'react';
import {
  IonContent,
  IonHeader,
  IonList,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
  IonButtons,
  IonThumbnail,
  IonImg,
  IonLabel,
  IonButton,
  IonIcon,
  IonPopover,
  IonItem,
  IonModal
} from '@ionic/react';
import './AllartSelectStyle.css';
import ProductImagesItem from '../components/ProductImagesItem';
import { Images, getProductImages } from '../data/ProductImages';
import { TransformWrapper, TransformComponent } from 'react-zoom-pan-pinch';

const ProductGalleryST1: React.FC = () => {

  const [ProductImages, setProductImages] = useState<Images[]>([]);
  const [showPopover, setShowPopover] = useState(false);
  const [showModal, setShowModal] = useState(false);
   const prevImage = () =>{

   }

    const nextImage = () =>{

   }

  useIonViewWillEnter(() => {
    const pimg = getProductImages();
    setProductImages(pimg);
  });

  const refresh = (e: CustomEvent) => {
    setTimeout(() => {
      e.detail.complete();
    }, 3000);
  };



  return (
    <IonPage id="product-gallery">
      <IonHeader>
        <IonToolbar>
        <IonButtons  slot="start">
          <IonButton href="/home/AllartSelect/">
            <IonIcon slot="icon-only" src="assets/icon/chevron-back-outline.svg"/>
            </IonButton>
        </IonButtons>
  <IonTitle>ST1</IonTitle>
          <IonButtons slot="end">
          <IonButton class="actionButton" onClick={() => setShowPopover(true)}>
            <IonIcon slot="icon-only" src="assets/icon/ellipsis-vertical-outline.svg"/>
          </IonButton>
        </IonButtons>
        <IonPopover
            isOpen={showPopover}
            cssClass='my-custom-class'
            onDidDismiss={e => setShowPopover(false)}>
            <a href="/Notifications"><IonIcon slot="icon-only" src="assets/icon/notifications-outline.svg"/>Notifications</a>
            <hr id="solid"></hr>
            <a href="/Search"><IonIcon slot="icon-only" src="assets/icon/search-outline.svg"/>Search</a>
            {/*<hr id="solid"></hr>
            <a><IonIcon slot="icon-only" src="assets/icon/thumbs-up-outline.svg"/>Rate Now</a>
            <hr id="solid"></hr>
  <a><IonIcon slot="icon-only" src="assets/icon/share-social-outline.svg"/>Share Now</a>*/}
          </IonPopover>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonRefresher slot="fixed" onIonRefresh={refresh}>
          <IonRefresherContent></IonRefresherContent>
        </IonRefresher>
        <IonList id="productGalleryTitles">
        <IonThumbnail id="productGalleryTitleImg">
          <IonImg src="assets/menu-images/ST.png"/>
        </IonThumbnail>
        <IonLabel><p>ST1</p></IonLabel>
        <IonLabel><p>15 Photos</p></IonLabel>
        </IonList>
        <IonList id="productGalleryls">

          {ProductImages.filter((pi) => pi.pageName === 'ST1').map((pi) => {



            return  <IonItem  id="prodImgItem">
            <IonThumbnail id="prodImage" slot="start">
              <IonImg class="productImgBtn" onClick={() => setShowModal(true)} src={pi.src} />
             <IonModal  isOpen={showModal} cssClass='productModal'>
              <IonHeader translucent>
            <IonToolbar>
              <IonButtons slot="end">
                <IonButton onClick={() => setShowModal(false)}>Close</IonButton>
              </IonButtons>
            </IonToolbar>
          </IonHeader>
          <IonContent>
          <TransformWrapper>
          <TransformComponent >
            <img src={pi.src} alt="productImage" />
          </TransformComponent>
          </TransformWrapper>
          <button onClick={prevImage}>&lt;</button>
          <button onClick={nextImage}>&gt;</button>
            <p id="prodTitle">
              {pi.title}
            </p>
          </IonContent>
          </IonModal>
          </IonThumbnail>
             </IonItem>;

          })}




        </IonList>
      </IonContent>
    </IonPage>
  );
};

export default ProductGalleryST1;
