import React, { useState } from 'react';
import { Menu, getMenuItems } from '../data/menuItems';
import {
  IonContent,
  IonHeader,
  IonList,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
  IonButtons,
  IonBackButton,
  IonGrid,
  IonRow,
  IonCol,
  IonCard,
  IonCardTitle,
  IonButton,
  IonIcon,
  IonPopover,
  IonSearchbar
} from '@ionic/react';
import './Search.css';
import MenuListItem from '../components/MenuListItem';
import { chevronBack, ellipsisVerticalOutline } from 'ionicons/icons';

const Search: React.FC = () => {
  const [searchText, setSearchText] = useState('');
  const [menuItems, setMenuItems] = useState<Menu[]>([]);
  const [showPopover, setShowPopover] = useState(false);

  useIonViewWillEnter(() => {
    const mnu = getMenuItems();
    setMenuItems(mnu);
  });


  return (
    <IonPage id="Miscellaneous">
      <IonHeader>
        <IonToolbar>
        <IonButtons  slot="start">
          <IonButton href="..">
            <IonIcon slot="icon-only" src="assets/icon/chevron-back-outline.svg"/>
            </IonButton>
        </IonButtons>
          <IonTitle>Search</IonTitle>
          <IonButtons slot="end">
          <IonButton class="actionButton" onClick={() => setShowPopover(true)}>
            <IonIcon slot="icon-only" src="assets/icon/ellipsis-vertical-outline.svg"/>
          </IonButton>
        </IonButtons>
        <IonPopover
            isOpen={showPopover}
            cssClass='my-custom-class'
            onDidDismiss={e => setShowPopover(false)}>
             <a href="/Notifications"><IonIcon slot="icon-only" src="assets/icon/notifications-outline.svg"/>Notifications</a>
            <hr id="solid"></hr>
            <a href="/Search"><IonIcon slot="icon-only" src="assets/icon/search-outline.svg"/>Search</a>
            <hr id="solid"></hr>
            <a><IonIcon slot="icon-only" src="assets/icon/thumbs-up-outline.svg"/>Rate Now</a>
            <hr id="solid"></hr>
            <a><IonIcon slot="icon-only" src="assets/icon/share-social-outline.svg"/>Share Now</a>
          </IonPopover>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
      <IonSearchbar value={searchText} autocomplete="off" showCancelButton="never"></IonSearchbar>
      <IonList>
      {menuItems.filter((m) => m.name == '{searchText}').map( m => <MenuListItem key={m.id} menu={m} />)}

        </IonList>
      </IonContent>
    </IonPage>


  );
};



export default Search;
