import React, { useState } from 'react';
import {
  IonItem,
  IonLabel,
  IonNote,
  IonThumbnail,
  IonList,
  IonImg,
  IonContent,
  IonPopover,
  IonIcon
  } from '@ionic/react';
import { Images } from '../data/ProductImages';
import './ProductImagesItem.css';
interface ProductImagesItemProps {
    productImages: Images;
}



const ProductImagesItem: React.FC<ProductImagesItemProps> = ({ productImages }) => {
  const [showPopover, setShowPopover] = useState(false);
  return (
        <IonItem id="prodImgItem">
          <IonThumbnail id="prodImage" slot="start">
            <IonImg class="productImgBtn" onClick={() => setShowPopover(true)} src={productImages.src} />
            <IonPopover
            isOpen={showPopover}
            cssClass='my-custom-class'
            onDidDismiss={e => setShowPopover(false)}>
            <a href="/Notifications"><IonIcon slot="icon-only" src="assets/icon/notifications-outline.svg"/>Notifications</a>
            <hr id="solid"></hr>
            <a href="/Search"><IonIcon slot="icon-only" src="assets/icon/search-outline.svg"/>Search</a>
            <hr id="solid"></hr>
            <a><IonIcon slot="icon-only" src="assets/icon/thumbs-up-outline.svg"/>Rate Now</a>
            <hr id="solid"></hr>
            <a><IonIcon slot="icon-only" src="assets/icon/share-social-outline.svg"/>Share Now</a>
          </IonPopover>
          </IonThumbnail>
        </IonItem>
  );

};

export default ProductImagesItem;
