import React, { useRef } from 'react'
import { IonSlides, IonSlide, IonImg } from '@ionic/react'
import '../styles/ProductSlider.css'

interface Slide {
	pageName: string
	src: string
	title: string
	id: number
}

interface ProductSliderProps {
	slides: Slide[]
	isOpen: boolean
	openOnSlide: number
	handleCloseSlider: () => void
}

const ProductSlider: React.FC<ProductSliderProps> = ({ slides, isOpen, openOnSlide, handleCloseSlider }) => {
	const slidesRef = useRef(null)

  const handleChangeSlide = async (_event: any, direction) => {
		console.log('direction', direction)
    const swiper = await slidesRef.current.getSwiper()
    swiper.slideNext()
  }

	// const handleChangeSlide = (direction) => {
	// 	console.log('direction', direction)
	// 	if (direction === 'next') {
	// 		slidesRef.current?.slideNext()
	// 	} else {
	// 		// Assume previous slide
	// 		slidesRef.current?.slidePrev()
	// 	}
	// }

	return (
		<div className="ProductSlider">
			<button
				className="ProductSlider__close"
				onClick={handleCloseSlider}
			>
				Close
			</button>
			<IonSlides ref={slidesRef}>
				{slides.map(slide => (
					<IonSlide key={slide.id}>
						<IonImg
							key={slide.id}
							class="ProductSlider__img"
							src={slide.src}
							/>
					</IonSlide>
				))}
			</IonSlides>
			<button onClick={() => handleChangeSlide('prev')}>Prev</button>
			<button onClick={() => handleChangeSlide('next')}>Next</button>
		</div>
	)
}

export default ProductSlider
