import React, { useState } from 'react';

import {
  IonItem,
  IonThumbnail,
  IonImg,
  IonContent,
  IonModal,
  IonButton,
  IonButtons,
  IonHeader,
  IonToolbar,
  } from '@ionic/react';
import { Images } from '../data/ProductImages';
import './ProductImagesItem.css';
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";

interface ProductImagesItemProps {
    productImages: Images;
}

const ProductImagesItem: React.FC<ProductImagesItemProps> = ({ productImages }) => {
  const [showModal, setShowModal] = useState(false);
  const [hasPrevProductImage, setHasPrevProductImage] = useState(true);
  const [hasNextProductImage, setHasNextProductImage] = useState(true);


  return (


        <IonItem id="prodImgItem">          
          <IonThumbnail id="prodImage" slot="start">
            <IonImg class="productImgBtn" id={"modal-" + productImages.id.toString()} onClick={() => setShowModal(true)} src={productImages.src} />
            <IonModal  isOpen={showModal} cssClass='productModal' onDidPresent={function(){if(!(productImages.id--)){setHasPrevProductImage(false);}}}>
              <IonHeader translucent>
            <IonToolbar>
              <IonButtons slot="end">
                <IonButton id={"close-btn-" + productImages.id.toString()} onClick={() => setShowModal(false)}>Close</IonButton>
              </IonButtons>
            </IonToolbar>
          </IonHeader>
          <IonContent>
          <TransformWrapper>
          <TransformComponent>
            {hasPrevProductImage &&
              <IonButton id={"prev-btn-" + productImages.id.toString()} onClick={() => previousSlide( (productImages.id).toString() ) }>Previous</IonButton>
            }

                <img src={productImages.src} alt="productImage" />

            {hasNextProductImage &&
              <IonButton id={"next-btn-" + productImages.id.toString()} onClick={() => nextSlide( (productImages.id).toString() ) }>Next</IonButton>
                }
          </TransformComponent>
          </TransformWrapper>
            <p id="prodTitle">
              {productImages.title}
            </p>
          </IonContent>
          </IonModal>
        </IonThumbnail>
      </IonItem>


  );
};




function previousSlide(id){
  console.log(id);
  document.getElementById("close-btn-" + id.toString()).click();
  id--;
  document.getElementById("modal-" + (id).toString()).click(); 
}

function nextSlide(id){
  document.getElementById("close-btn-" + id.toString()).click();
  id++;
  document.getElementById("modal-" + (id).toString()).click();
}




export default ProductImagesItem;
