import React, { useState } from 'react';
import {
  IonContent,
  IonHeader,
  IonList,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
  IonButtons,
  IonBackButton,
  IonThumbnail,
  IonItem,
  IonImg,
  IonLabel,
  IonButton,
  IonIcon,
  IonPopover
} from '@ionic/react';
import './ProductGallery.css';
import ProductImagesItem from '../components/ProductImagesItem';
import { ellipsisVerticalOutline, chevronBack } from 'ionicons/icons';
import { Images, getProductImages } from '../data/ProductImages';

const ProductGallery: React.FC = () => {

  const [ProductImages, setProductImages] = useState<Images[]>([]);
  const [showPopover, setShowPopover] = useState(false);

  useIonViewWillEnter(() => {
    const pimg = getProductImages();
    setProductImages(pimg);
  });

  const refresh = (e: CustomEvent) => {
    setTimeout(() => {
      e.detail.complete();
    }, 3000);
  };

  return (
    <IonPage id="product-gallery">
      <IonHeader>
        <IonToolbar>
        <IonButtons  slot="start">
          <IonButton href="..">
            <IonIcon slot="icon-only" src="assets/icon/chevron-back-outline.svg"/>
            </IonButton>
        </IonButtons>
          <IonTitle>TEST</IonTitle>
          <IonButtons slot="end">
          <IonButton>
            <IonIcon slot="icon-only" src="assets/icon/ellipsis-vertical-outline.svg"/>
          </IonButton>
        </IonButtons>
        <IonPopover
            isOpen={showPopover}
            cssClass='my-custom-class'
            onDidDismiss={e => setShowPopover(false)}>
            <a>Notifications</a>
            <hr id="solid"></hr>
            <a>Settings</a>
          </IonPopover>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonRefresher slot="fixed" onIonRefresh={refresh}>
          <IonRefresherContent></IonRefresherContent>
        </IonRefresher>
        <IonList id="productGalleryTitles">
        <IonThumbnail id="productGalleryTitleImg">
          <IonImg src=""/>
        </IonThumbnail>
        <IonLabel><p>TEST</p></IonLabel>
        <IonLabel><p>TEST</p></IonLabel>
        </IonList>
        <IonList id="productGalleryls">
         {ProductImages.map(pi => <ProductImagesItem key={pi.id} productImages={pi} />)}

        </IonList>
      </IonContent>
    </IonPage>
  );
};

export default ProductGallery;
