import React from 'react'
import { IonSlides, IonSlide, IonModal, IonImg } from '@ionic/react'
import ProductImagesItem from '../components/ProductImagesItem'

interface Slide {
	pageName: string
	src: string
	title: string
	id: number
}

interface ProductSliderProps {
	slides: Slide[]
	isOpen: boolean
}

const ProductSlider: React.FC<ProductSliderProps> = ({ slides, isOpen }) => {
	return (
		<IonModal
			isOpen={isOpen}
			cssClass='productModal'
		>
			<IonSlides>
				{slides.map(slide => (
					<IonSlide key={slide.id}>
						<ProductImagesItem
							key={slide.id}
							productImages={slide}
						/>
						<IonImg class="ProductThumbnail__img productImgBtn" src={slide.src} />
					</IonSlide>
				))}
			</IonSlides>
		</IonModal>
	)
}

export default ProductSlider
