import React, { useState } from 'react';
import { Menu, getMenuItems } from '../data/menuItems';
import {
  IonContent,
  IonHeader,
  IonList,
  IonPage,
  IonRefresher,
  IonRefresherContent,
  IonTitle,
  IonToolbar,
  useIonViewWillEnter,
  IonButtons,
  IonBackButton
} from '@ionic/react';
import './ProductGallery.css';
import ProductImagesItem from '../components/ProductImagesItem';
import { ellipsisVerticalOutline } from 'ionicons/icons';
import { Images, getProductImages } from '../data/ProductImages';

const ProductGallery: React.FC = () => {

  const [ProductImages, setProductImages] = useState<Images[]>([]);

  useIonViewWillEnter(() => {
    const pimg = getProductImages();
    setProductImages(pimg);
  });

  const refresh = (e: CustomEvent) => {
    setTimeout(() => {
      e.detail.complete();
    }, 3000);
  };

  return (
    <IonPage id="product-gallery">
      <IonHeader>
        <IonToolbar>
          <IonTitle> TEST</IonTitle>
          <IonButtons  slot="end">
          <IonBackButton defaultHref="/home" icon={ellipsisVerticalOutline} />
        </IonButtons>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonRefresher slot="fixed" onIonRefresh={refresh}>
          <IonRefresherContent></IonRefresherContent>
        </IonRefresher>

        <IonList>
         {ProductImages.map(pi => <ProductImagesItem key={pi.id} productImages={pi} />)}
        </IonList>
      </IonContent>
    </IonPage>
  );
};

export default ProductGallery;
