import React, { useState } from 'react';
import {
  IonItem,
  IonLabel,
  IonNote,
  IonThumbnail,
  IonList,
  IonImg,
  IonContent,
  IonPopover,
  IonIcon
  } from '@ionic/react';
import { Images } from '../data/ProductImages';
import './ProductImagesItem.css';
interface ProductImagesItemProps {
    productImages: Images;
}



const ProductImagesItem: React.FC<ProductImagesItemProps> = ({ productImages }) => {
  const [showPopover, setShowPopover] = useState(false);
  return (
        <IonItem id="prodImgItem">
          <IonThumbnail id="prodImage" slot="start">
            <IonImg class="productImgBtn" onClick={() => setShowPopover(true)} src={productImages.src} />
            <IonPopover
            isOpen={showPopover}
            cssClass='productImgPopUp'
            onDidDismiss={e => setShowPopover(false)}>
                <IonImg src={productImages.src} />
          </IonPopover>
          </IonThumbnail>
        </IonItem>
  );

};

export default ProductImagesItem;
