import React, { useState } from 'react'
import {
  IonItem,
  IonThumbnail,
  IonImg
} from '@ionic/react'

interface ProductThumbnailProps {
	id: number
	handleOpenSlider: () => void
}

const ProductThumbnail: React.FC<ProductThumbnailProps> = ({ id, handleOpenSlider }) => {
  return (
    <IonItem className="ProductThumbnail">
			<IonThumbnail className="ProductThumbnail__inner">
				<button
					key={id}
					className="ProductThumbnail__btn u-btn-clear"
					onClick={handleOpenSlider}
				>
					<IonImg class="ProductThumbnail__img productImgBtn" src={pi.src} />
				</button>
			</IonThumbnail>
		</IonItem>
  )
}

export default ProductThumbnail
