import React from 'react'
import { IonSlides, IonSlide} from '@ionic/react'
import '../styles/ProductSlider.css'
import { TransformWrapper, TransformComponent } from 'react-zoom-pan-pinch'

interface Slide {
	pageName: string
	src: string
	title: string
	id: number
}

interface ProductSliderProps {
	slides: Slide[]
}

const ProductSlider: React.FC<ProductSliderProps> = ({ slides}) => {

	const slideOpt ={
		initialSlide: 0
	}


	return (
		<div className="ProductSlider">
				<IonSlides
				options={slideOpt}>
				{slides.map(slide => (
						<IonSlide key={slide.id}>
							<TransformWrapper scale={1} positionX={200} positionY={100} >

							{({ zoomIn, zoomOut, resetTransform, ...rest }) => (
								<React.Fragment >
									<div onLoad={resetTransform}></div>
								<TransformComponent >
									<img alt={slide.title} className="slideImg"
										key={slide.id}
										src={slide.src} />
								</TransformComponent>
								</React.Fragment>
							)}
							</TransformWrapper>
							<p style={{marginTop:'4px'}}>{slide.title}</p>
						</IonSlide>
				))}

			</IonSlides>
		</div>
	)
}

export default ProductSlider
